<?php

function get_first_basement_entry($str)
{
    $basement_floor = -1;
    $current_floor  = 0;

    foreach (str_split($str) as $x => $input)
    {
        $current_floor += match($input)
        {
            ')' => -1,
            '(' => +1,
            default => 0,
        };

        if ($current_floor == $basement_floor)
        {
            return $x+1;
        }
    }

    return null;
}

